AnimatedModalBarrier(
  color: _controller.drive(ColorTween(begin: Colors.transparent, end: Colors.black54)),
  dismissible: true,
);

AnimatedOpacity(
  opacity: _visible ? 1.0 : 0.0,
  duration: Duration(seconds: 1),
  child: Text("Fade Me"),
);

AnimatedPadding(
  padding: _padding,
  duration: Duration(seconds: 1),
  child: Container(color: Colors.blue),
);

AnimatedPositioned(
  left: _left,
  top: _top,
  duration: Duration(seconds: 1),
  child: Container(width: 50, height: 50, color: Colors.red),
);

AnimatedRotation(
  turns: _turns,
  duration: Duration(seconds: 1),
  child: Icon(Icons.refresh),
);

AnimatedSize(
  duration: Duration(seconds: 1),
  child: Container(height: _height, width: _width),
);

AnimatedSwitcher(
